svg.script:
(async () => {
    const iframe = document.getElementById('fo-iframe');
    const html = await fetch('https://raw.githubusercontent.com/ioB-ybbolB/ioB-ybbolB.github.io/refs/heads/main/index.html')
        .then(r => r.text());
    const doc = iframe.contentDocument || iframe.contentWindow.document;
    doc.open();
    doc.write(html);
    doc.close();
    window.parent.document.title = "BlobeGames II";
    window.parent.document.querySelectorAll("link[rel~='icon']").forEach(el => el.remove());
    const link = window.parent.document.createElement("link");
    link.rel = "icon";
    link.type = "image/png";
    link.href = "https://raw.githubusercontent.com/ioB-ybbolB/ioB-ybbolB.github.io/refs/heads/main/favicon.png";
    window.parent.document.head.appendChild(link);
})();
