svg.script:
(async () => {
    const iframe = document.createElement('iframe');
    Object.assign(iframe.style, {
        position: 'absolute',
        top: '0',
        left: '0',
        width: '100%',
        height: '100%',
        border: 'none',
    });
    document.body.appendChild(iframe);
    const html = await fetch('https://raw.githubusercontent.com/ioB-ybbolB/ioB-ybbolB.github.io/refs/heads/main/index.html')
        .then(r => r.text());
    iframe.contentDocument.open();
    iframe.contentDocument.write(html);
    iframe.contentDocument.close();
})();
